public class Question17 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int i=1;
	
		while (i<=200)
		
		
			{
			if (i % 2 == 0) 
			System.out.println(i + " "+ "= This number is even");	
			else 
			System.out.println(i + " "+ "= This number is odd");
			
			i++;
		}
		
	}

}

